Insert into JAVER.SERIES_VODS (VOD_NO,CATEGORY_NO,VOD_IMG,VOD_RUNNING_TIME,VOD_AIR_START_DATE,VOD_AIR_FINISH_DATE,VOD_GRADE,VOD_ACTOR,VOD_DIRECTOR,VOD_DESCRIPTION,VOD_TITLE,VOD_PRICE,VOD_CREATE_DATE,VOD_IS_END) values (56,38,'안녕자두야 스페셜 - 인어공주.jpg',30,to_date('13/12/23','RR/MM/DD'),to_date('13/12/24','RR/MM/DD'),'전체관람가','연여민정','손석우','대한민국을 사로잡은 명랑소녀 최자두가 인어공주로 돌아왔다. 어부인 돌돌과 성훈에게 붙잡힌 자두 공주! 시장에서 구경거리가 된 자두 공주를 윤석 왕자가 구해주고, 둘의 사랑은 깊어만 가는데. 방해꾼 은희 공주의 등장으로 자두 공주와 윤석 왕자의 운명은 어떻게 될 것인가!','안녕자두야 스페셜 - 인어공주',800,to_date('19/07/30','RR/MM/DD'),'(종영)');
